    @Test
    public void patternCount() {
        //should be 2
        int count = Utilities.PatternCount("GCGCG", "GCG");
        System.out.println(count);
        Assert.assertEquals(count, 2);

        //should be 21
        count = Utilities.PatternCount("", "CTGGAACCT");
        System.out.println(count);
        Assert.assertEquals(count, 21);
        System.out.println(Utilities.PatternCount("GACCATCAAAACTGATAAACTACTTAAAAATCAGT", "AAA"));
    }

    @Test
    public void frequentWords() {
        //should be 2 sized Arraylist
        List<String> frequentWords = Utilities.FrequentWords("ACGTTGCATGTCGCATGATGCATGAGAGCT", 4);
        System.out.println(frequentWords);
        Assert.assertTrue(frequentWords.contains("CATG"));
        Assert.assertTrue(frequentWords.contains("GCAT"));
        Assert.assertEquals(2, frequentWords.size());

        //should be 6 sized Arraylist
        frequentWords = Utilities.FrequentWords("", 12);
        System.out.println(frequentWords);
        //[GGCAAACGACGG, AGCCGCTCAGCC, GCCGCTCAGCCG, CCGCTCAGCCGC, CGCTCAGCCGCT, GCTCAGCCGCTC]
        Assert.assertTrue(frequentWords.contains("GGCAAACGACGG"));
        Assert.assertTrue(frequentWords.contains("AGCCGCTCAGCC"));
        Assert.assertTrue(frequentWords.contains("GCCGCTCAGCCG"));
        Assert.assertTrue(frequentWords.contains("CCGCTCAGCCGC"));
        Assert.assertTrue(frequentWords.contains("CGCTCAGCCGCT"));
        Assert.assertTrue(frequentWords.contains("GCTCAGCCGCTC"));
        Assert.assertEquals(6, frequentWords.size());

        System.out.println(Utilities.FrequentWords("CGCCTAAATAGCCTCGCGGAGCCTTATGTCATACTCGTCCT", 3));
    }

    @Test
    public void patternToNumber() {
        Assert.assertEquals(0, Utilities.PatternToNumber("A"));
        Assert.assertEquals(1, Utilities.PatternToNumber("C"));
        Assert.assertEquals(2, Utilities.PatternToNumber("G"));
        Assert.assertEquals(3, Utilities.PatternToNumber("T"));
        Assert.assertEquals(0, Utilities.PatternToNumber("AA"));
        Assert.assertEquals(0, Utilities.PatternToNumber("AAA"));
        Assert.assertEquals(12, Utilities.PatternToNumber("ATA"));
        Assert.assertEquals(912, Utilities.PatternToNumber("ATGCAA"));
        Assert.assertEquals(26748261, Utilities.PatternToNumber("AAACGCGAAGCCCGCC"));

    }

    @Test
    public void numberToPattern() {
        Assert.assertEquals("A", Utilities.NumberToPattern(0, 1));
        Assert.assertEquals("C", Utilities.NumberToPattern(1, 1));
        Assert.assertEquals("G", Utilities.NumberToPattern(2, 1));
        Assert.assertEquals("T", Utilities.NumberToPattern(3, 1));
        Assert.assertEquals("AA", Utilities.NumberToPattern(0, 2));
        Assert.assertEquals("AAA", Utilities.NumberToPattern(0, 3));
        Assert.assertEquals("ATA", Utilities.NumberToPattern(12, 3));
        Assert.assertEquals("CCCATTC", Utilities.NumberToPattern(5437, 7));
        Assert.assertEquals("ACCCATTC", Utilities.NumberToPattern(5437, 8));
        Assert.assertEquals("AGT", Utilities.NumberToPattern(11, 3));
        Assert.assertEquals("AAAACTAAATA", Utilities.NumberToPattern(7180, 11));

    }

